import React, { useState, useEffect } from 'react';

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', description: '', price: '', quantity: '' });
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');
  
  const token = localStorage.getItem('token');
  const userId = localStorage.getItem('userId');
  const isAdmin = localStorage.getItem('isAdmin') === 'true';

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/products', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (!res.ok) throw new Error('Failed to fetch products');
      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      setError('Failed to fetch products');
      console.error('Fetch error:', err);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'quantity') {
      if (value === '' || (/^\d+$/.test(value) && parseInt(value) > 0)) {
        setForm({ ...form, quantity: value });
      }
      return;
    }
    
    if (name === 'price') {
      if (value === '' || (/^\d*\.?\d*$/.test(value) && parseFloat(value) >= 0)) {
        setForm({ ...form, price: value });
      }
      return;
    }
    
    setForm({ ...form, [name]: value });
  };

  const validateForm = () => {
    const priceValue = parseFloat(form.price);
    const quantityValue = parseInt(form.quantity);

    if (!form.name || !form.description || !priceValue || !quantityValue) {
      setError('All fields are required');
      return false;
    }
    
    if (priceValue <= 0 || quantityValue <= 0) {
      setError('Price and quantity must be greater than 0');
      return false;
    }
    
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!validateForm()) {
      return;
    }

    try {
      const url = editingId 
        ? `http://localhost:5000/api/products/${editingId}`
        : 'http://localhost:5000/api/products';

      const response = await fetch(url, {
        method: editingId ? 'PUT' : 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(form),
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.message || 'Failed to save product');
      }

      setForm({ name: '', description: '', price: '', quantity: '' });
      setEditingId(null);
      fetchProducts();
    } catch (err) {
      setError(err.message);
      console.error('Submit error:', err);
    }
  };

  const handleEdit = (product) => {
    setForm(product);
    setEditingId(product.id);
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.message || 'Failed to delete product');
      }

      fetchProducts();
    } catch (err) {
      setError(err.message);
      console.error('Delete error:', err);
    }
  };

  const canModifyProduct = (productUserId) => {
    return isAdmin || productUserId === userId;
  };

  return (
    <div className="bg-gray-100 min-h-screen pt-20">
      <main className="max-w-7xl mx-auto p-4">
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
            {error}
            <button
              className="absolute top-0 right-0 px-4 py-3"
              onClick={() => setError('')}
            >
              ×
            </button>
          </div>
        )}
        
        <div className="flex gap-6">
          {/* Left side - Form */}
          <div className="w-1/3">
            <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md sticky top-24">
              <h2 className="text-xl font-semibold mb-4">{editingId ? 'Edit Product' : 'Add New Product'}</h2>
              <div className="space-y-4">
                <input
                  name="name"
                  placeholder="Name"
                  onChange={handleChange}
                  value={form.name}
                  className="border p-2 rounded w-full"
                />
                <input
                  name="description"
                  placeholder="Description"
                  onChange={handleChange}
                  value={form.description}
                  className="border p-2 rounded w-full"
                />
                <input
                  name="price"
                  placeholder="Price"
                  onChange={handleChange}
                  value={form.price}
                  className="border p-2 rounded w-full"
                />
                <input
                  name="quantity"
                  placeholder="Quantity"
                  type="number"
                  onChange={handleChange}
                  value={form.quantity}
                  className="border p-2 rounded w-full"
                />
              </div>
              <button type="submit" className="bg-blue-500 text-white p-2 mt-4 rounded w-full">
                {editingId ? 'Update Product' : 'Add Product'}
              </button>
            </form>
          </div>

          {/* Right side - Product List */}
          <div className="w-2/3">
            <ul className="space-y-4">
              {products.length === 0 ? (
                <li className="bg-white p-4 rounded shadow-md text-center text-gray-500">
                  No products found
                </li>
              ) : (
                products.map((product) => (
                  <li key={product.id} className="bg-white p-4 rounded shadow-md flex justify-between items-center">
                    <div>
                      <h3 className="font-bold">{product.name}</h3>
                      <p>{product.description}</p>
                      <p>₹{product.price}</p>
                      <p>Quantity: {product.quantity}</p>
                    </div>
                    {canModifyProduct(product.user_id) && (
                      <div>
                        <button
                          onClick={() => handleEdit(product)}
                          className="bg-yellow-500 text-white p-2 mr-2 rounded"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDelete(product.id)}
                          className="bg-red-500 text-white p-2 rounded"
                        >
                          Delete
                        </button>
                      </div>
                    )}
                  </li>
                ))
              )}
            </ul>
          </div>
        </div>
      </main>
    </div>
  );
}

export default ProductManagement;
