import React, { useState, useEffect } from 'react';

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', description: '', price: '', quantity: '' });
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      setError('');
      const res = await fetch('http://localhost:5000/api/products', {
        headers: { 'Content-Type': 'application/json' },
      });

      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);

      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Fetch error:', err);
      setError('Failed to fetch products');
      setProducts([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleEdit = (product) => {
    setForm({
      name: product.name,
      description: product.description,
      price: product.price,
      quantity: product.quantity,
    });
    setEditingId(product.id);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const url = editingId
        ? `http://localhost:5000/api/products/${editingId}`
        : 'http://localhost:5000/api/products';

      const response = await fetch(url, {
        method: editingId ? 'PUT' : 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.message || 'Failed to save product');
      }

      setForm({ name: '', description: '', price: '', quantity: '' });
      setEditingId(null);
      fetchProducts();
    } catch (err) {
      setError(err.message);
      console.error('Submit error:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.message || 'Failed to delete product');
      }

      fetchProducts();
    } catch (err) {
      setError(err.message);
      console.error('Delete error:', err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-100 to-indigo-200 p-10">
      <main className="max-w-3xl mx-auto bg-white rounded-lg shadow-lg p-8">
        {error && (
          <div className="mb-6 p-4 rounded-lg bg-red-100 border border-red-400 text-red-600 relative">
            {error}
            <button
              className="absolute top-1 right-1 text-red-600 font-bold"
              onClick={() => setError('')}
            >
              &times;
            </button>
          </div>
        )}

        {/* Form Section */}
        <form onSubmit={handleSubmit} className="bg-gray-50 p-6 rounded-lg shadow-md mb-10">
          <h2 className="text-2xl font-semibold mb-4 text-indigo-700">
            {editingId ? 'Edit Product' : 'Add New Product'}
          </h2>
          <div className="space-y-4">
            <input
              name="name"
              placeholder="Product Name"
              onChange={handleChange}
              value={form.name}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
            <input
              name="description"
              placeholder="Description"
              onChange={handleChange}
              value={form.description}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
            <input
              name="price"
              placeholder="Price"
              type="number"
              onChange={handleChange}
              value={form.price}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
            <input
              name="quantity"
              placeholder="Quantity"
              type="number"
              onChange={handleChange}
              value={form.quantity}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
            />
          </div>
          <button
            type="submit"
            className="w-full mt-6 py-2 px-4 bg-indigo-500 text-white font-semibold rounded-lg hover:bg-indigo-600 transition-colors"
          >
            {editingId ? 'Update Product' : 'Add Product'}
          </button>
        </form>

        {/* Product List Section */}
        {loading ? (
          <div className="bg-white p-6 rounded-lg shadow-lg text-center text-indigo-700 font-semibold">
            Loading products...
          </div>
        ) : (
          <ul className="space-y-4">
            {products.length === 0 ? (
              <li className="bg-white p-6 rounded-lg shadow-lg text-center text-gray-500">
                {error ? 'Error loading products' : 'No products found'}
              </li>
            ) : (
              products.map((product) => (
                <li
                  key={product.id}
                  className="bg-white p-6 rounded-lg shadow-lg flex flex-col items-start space-y-2"
                >
                  <h3 className="text-xl font-semibold text-indigo-700">{product.name}</h3>
                  <p className="text-gray-500">{product.description}</p>
                  <p className="text-lg text-indigo-600 font-semibold">â‚¹{product.price}</p>
                  <p className="text-sm text-gray-400">Quantity: {product.quantity}</p>
                  <div className="flex gap-4 mt-4">
                    <button
                      onClick={() => handleEdit(product)}
                      className="px-4 py-2 bg-yellow-400 hover:bg-yellow-500 text-white font-semibold rounded-lg"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(product.id)}
                      className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white font-semibold rounded-lg"
                    >
                      Delete
                    </button>
                  </div>
                </li>
              ))
            )}
          </ul>
        )}
      </main>
    </div>
  );
}

export default ProductManagement;
