import React, { useState, useEffect } from 'react';

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', description: '', price: '', quantity: '' });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await fetch('http://localhost:5000/products');
    const data = await res.json();
    setProducts(data);
  };

const handleChange = (e) => {
  const { name, value } = e.target;
  
  if (name === 'quantity') {
    if (value === '' || (/^\d+$/.test(value) && parseInt(value) > 0)) {
      setForm({ ...form, quantity: value });
    }
    return;
  }
  
  if (name === 'price') {
    if (value === '' || (/^\d*\.?\d*$/.test(value) && parseFloat(value) >= 0)) {
      setForm({ ...form, price: value });
    }
    return;
  }
  
  setForm({ ...form, [name]: value });
};

  const validateForm = () => {
    const priceValue = parseFloat(form.price);
    const quantityValue = parseInt(form.quantity);
    if ((quantityValue > 0 && Number.isInteger(quantityValue))) {
    }
    else{
      return false;
    }
    if (priceValue > 0) {
    }
    else{
      return false;
    }

    if (!form.name || !form.description || !priceValue || !quantityValue) {
      return false;
    }
    
    if (priceValue <= 0 || quantityValue <= 0) {
      return false;
    }
    
    return true;
  };
  

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      alert('Please check all fields are filled correctly');
      return;
    }

    if (editingId) {
      await fetch(`http://localhost:5000/products/${editingId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      setEditingId(null);
    } else {
      await fetch('http://localhost:5000/products', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
    }
    setForm({ name: '', description: '', price: '', quantity: '' });
    fetchProducts();
  };

  const handleEdit = (product) => {
    setForm(product);
    setEditingId(product.id);
  };

  const handleDelete = async (id) => {
    await fetch(`http://localhost:5000/products/${id}`, { method: 'DELETE' });
    fetchProducts();
  };

  return (
    <div className="bg-gray-100 min-h-screen pt-20">
      <main className="max-w-7xl mx-auto p-4">
        <div className="flex gap-6">
          {/* Left side - Form */}
          <div className="w-1/3">
            <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md sticky top-24">
              <h2 className="text-xl font-semibold mb-4">{editingId ? 'Edit Product' : 'Add New Product'}</h2>
              <div className="space-y-4">
                <input
                  name="name"
                  placeholder="Name"
                  onChange={handleChange}
                  value={form.name}
                  className="border p-2 rounded w-full"
                />
                <input
                  name="description"
                  placeholder="Description"
                  onChange={handleChange}
                  value={form.description}
                  className="border p-2 rounded w-full"
                />
                <input
                  name="price"
                  placeholder="Price"
                  onChange={handleChange}
                  value={form.price}
                  className="border p-2 rounded w-full"
                />
                <input
                  name="quantity"
                  placeholder="Quantity"
                  type="number"
                  onChange={handleChange}
                  value={form.quantity}
                  className="border p-2 rounded w-full"
                />
              </div>
              <button type="submit" className="bg-blue-500 text-white p-2 mt-4 rounded w-full">
                {editingId ? 'Update Product' : 'Add Product'}
              </button>
            </form>
          </div>

          {/* Right side - Product List */}
          <div className="w-2/3">
            <ul className="space-y-4">
              {products.map((product) => (
                <li key={product.id} className="bg-white p-4 rounded shadow-md flex justify-between items-center">
                  <div>
                    <h3 className="font-bold">{product.name}</h3>
                    <p>{product.description}</p>
                    <p>₹{product.price}</p>
                    <p>Quantity: {product.quantity}</p>
                  </div>
                  <div>
                    <button
                      onClick={() => handleEdit(product)}
                      className="bg-yellow-500 text-white p-2 mr-2 rounded"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(product.id)}
                      className="bg-red-500 text-white p-2 rounded"
                    >
                      Delete
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </main>
    </div>
  );
}

export default ProductManagement;
