import React, { useState, useEffect } from 'react';

function ProductManagement() {
    const [products, setProducts] = useState([]);  // Initialize as empty array
    const [currentProduct, setCurrentProduct] = useState({ name: '', description: '', price: '' });
    const [isEditing, setIsEditing] = useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);  // Add loading state
    
    const userId = localStorage.getItem('userId');
    const isAdmin = localStorage.getItem('isAdmin') === 'true';
    const token = localStorage.getItem('token');

    // Fetch products
    const fetchProducts = async () => {
        try {
            setLoading(true);  // Set loading to true before fetch
            const response = await fetch('http://localhost:5000/api/products', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            
            if (!response.ok) {
                throw new Error('Failed to fetch products');
            }
            
            const data = await response.json();
            
            // Ensure data is an array
            if (Array.isArray(data)) {
                setProducts(data);
            } else {
                console.error('Received non-array data:', data);
                setProducts([]);
                setError('Invalid data format received');
            }
        } catch (err) {
            console.error('Fetch error:', err);
            setError('Failed to fetch products');
            setProducts([]);  // Set to empty array on error
        } finally {
            setLoading(false);  // Set loading to false after fetch
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const url = isEditing 
                ? `http://localhost:5000/api/products/${currentProduct.id}`
                : 'http://localhost:5000/api/products';
            
            const method = isEditing ? 'PUT' : 'POST';
            
            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(currentProduct)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to save product');
            }

            await fetchProducts();  // Refresh products list
            setCurrentProduct({ name: '', description: '', price: '' });
            setIsEditing(false);
        } catch (err) {
            console.error('Submit error:', err);
            setError(err.message || 'Failed to save product');
        }
    };

    // Handle delete
    const handleDelete = async (id) => {
        try {
            const response = await fetch(`http://localhost:5000/api/products/${id}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to delete product');
            }

            await fetchProducts();  // Refresh products list
        } catch (err) {
            console.error('Delete error:', err);
            setError(err.message || 'Failed to delete product');
        }
    };

    // Check if user can edit/delete product
    const canModifyProduct = (productUserId) => {
        return isAdmin || productUserId === userId;
    };

    if (loading) {
        return (
            <div className="container mx-auto px-4 pt-20">
                <div className="text-center">Loading...</div>
            </div>
        );
    }

    return (
        <div className="container mx-auto px-4 pt-20">
            {error && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
                    {error}
                    <button
                        className="absolute top-0 right-0 px-4 py-3"
                        onClick={() => setError('')}
                    >
                        ×
                    </button>
                </div>
            )}
            
            <form onSubmit={handleSubmit} className="mb-8">
                <div className="mb-4">
                    <input
                        type="text"
                        placeholder="Product Name"
                        value={currentProduct.name}
                        onChange={(e) => setCurrentProduct({...currentProduct, name: e.target.value})}
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                    />
                </div>
                <div className="mb-4">
                    <textarea
                        placeholder="Description"
                        value={currentProduct.description}
                        onChange={(e) => setCurrentProduct({...currentProduct, description: e.target.value})}
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                    />
                </div>
                <div className="mb-4">
                    <input
                        type="number"
                        placeholder="Price"
                        value={currentProduct.price}
                        onChange={(e) => setCurrentProduct({...currentProduct, price: e.target.value})}
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                    />
                </div>
                <button
                    type="submit"
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                    {isEditing ? 'Update Product' : 'Add Product'}
                </button>
            </form>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {products.length === 0 ? (
                    <div className="col-span-full text-center text-gray-500">
                        No products found.
                    </div>
                ) : (
                    products.map(product => (
                        <div key={product.id} className="border rounded-lg p-4 shadow">
                            <h3 className="text-xl font-bold">{product.name}</h3>
                            <p className="text-gray-600">{product.description}</p>
                            <p className="text-lg font-semibold">${product.price}</p>
                            
                            {canModifyProduct(product.user_id) && (
                                <div className="mt-4">
                                    <button
                                        onClick={() => {
                                            setCurrentProduct(product);
                                            setIsEditing(true);
                                        }}
                                        className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-1 px-2 rounded mr-2"
                                    >
                                        Edit
                                    </button>
                                    <button
                                        onClick={() => handleDelete(product.id)}
                                        className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded"
                                    >
                                        Delete
                                    </button>
                                </div>
                            )}
                        </div>
                    ))
                )}
            </div>
        </div>
    );
}

export default ProductManagement;
